commands:
domain=$(aws ssm get-parameters --names /domain/name --region ap-south-1 --query 'Parameters[0].Value' --output text)
ouPath=$(aws ssm get-parameters --names /domain/ou_path --region ap-south-1 --query 'Parameters[0].Value' --output text)
username=$(aws ssm get-parameters --names /domain/username --region ap-south-1 --query 'Parameters[0].Value' --output text)
password=$(aws ssm get-parameters --names /domain/password --with-decryption --region ap-south-1 --query 'Parameters[0].Value' --output text)
for i in 1 2 3 4 5;
do
echo $password | sudo realm join --membership-software=adcli -U $username --computer-ou=$ouPath $domain && echo "Host has joined domain successfully after $i retries" && break;
done
if ! sudo realm list |grep $domain; then echo "Host has not joined $domain so exiting"; exit -1; fi;
sudo shutdown -r 1
